import { Component } from '@angular/core';
import { Books } from '../Books';
import { BookService } from '../book.service';
import { Router } from '@angular/router';
import { BooksService } from '../books.service';


@Component({
  selector: 'app-modify',
  templateUrl: './modify.component.html',
  styleUrls: ['./modify.component.css']
})
export class ModifyComponent {

  constructor(private bs:BooksService,private router:Router){

  }
 book:Books=new Books();
 bookfound!:boolean;
  modify(){
   this.bookfound= this.bs.modify(this.book);
   if(this.bookfound==true){
    alert("book modified");
    this.router.navigate(['/viewall']);
   }
   else{
    alert("book id doesnot exist");
   }
  }
}
<p>modify works!</p>

Enter Bookid<input type=number [(ngModel)]="book.BookId">
Enter BookName<input type="text" [(ngModel)]="book.BookName">
Enter BookPrice<input type=number [(ngModel)]="book.BookPrice">
<button (click)="modify()">Modify</button>
