import { Component } from '@angular/core';
import { BooksService } from '../books.service';
import { Books } from '../Books';

@Component({
  selector: 'app-view',
  templateUrl: './view.component.html',
  styleUrls: ['./view.component.css']
})
export class ViewComponent {

  constructor(private bs:BooksService){

  }

  bookid!:number;
  bookfound!:Books;
  view(){
    this.bookfound=this.bs.view(this.bookid);
    if(this.bookfound==undefined){
      alert("book found");

    }
  }

}
<p>view works!</p>
Enter book to view<input type=number [(ngModel)]="bookid">
<button (click)="view()">View</button>
<table>
    <tr>
        <th>Bookid</th>
        <th>BookName</th>
        <th>Bookprice</th>
    </tr>
    <tr>
      <td><input type=number [(ngModel)]="bookfound.BookId"></td>
      <td><input type="text" [(ngModel)]="bookfound.BookName"></td>

    </tr>
</table>
