import { Component } from '@angular/core';

@Component({
  selector: 'app-likedislike',
  templateUrl: './likedislike.component.html',
  styleUrls: ['./likedislike.component.css']
})
export class LikedislikeComponent {

  likecount=0;
  likebutton="likebuttonstyle";

  dislikecount=0;
  dislikebutton="dislikebuttonstyle";

  increaseLikes(){
    this.likecount++;
    if(this.likecount==10){
      this.likecount=0;
    }
  }

  decreaseLikes(){
    this.dislikecount++;
    if(this.dislikecount==10){
      this.dislikecount=0;
    }
  }

}
