import { Component } from '@angular/core';
import { Bank } from '../Bank';
import { TransferService } from '../transfer.service';

@Component({
  selector: 'app-bank-account',
  templateUrl: './bank-account.component.html',
  styleUrls: ['./bank-account.component.css']
})
export class BankAccountComponent {
  sourceAccount:Bank={AccountNumber:101,AccountHolderName:"NIvi",AccountBalance:20000}
  targetAccount:Bank={AccountNumber:102,AccountHolderName:"Yashu",AccountBalance:1000}
  amountToTransfer!:number;

  constructor(private fts:TransferService){

  }


transfer(){
  this.fts.transferFunds(this.sourceAccount,this.targetAccount,this.amountToTransfer);
}

}
