@Pipe({
  name: 'palindrome'
})
export class PalindromePipe implements PipeTransform {

  transform(value:number): string {
     const temp=value;
     let rem:number;
     let rev=0;
     while(value>0){
      rem=value%10;
      rev=(rev*10)+rem;
      value=Math.floor(value/10);
     }
       if(temp==rev){
        return "palindrome";
       }
       else{
       return "no";
       }

  }

}
