import { Component } from '@angular/core';
import { BookService } from '../book.service';
import { Book } from '../Book';

@Component({
  selector: 'app-modify-book',
  templateUrl: './modify-book.component.html',
  styleUrls: ['./modify-book.component.css']
})
export class MOdifyBookComponent {

  constructor(private bookservice:BookService){

  }

  booktomodify:Book=new Book()
  bookfound!:boolean;
  modify(){
    this.bookservice.modify(this.booktomodify);
    if(this.bookfound==true){
      alert("book modified")
    }
    else{
      alert("book id does not exist")
    }
  }
}
