import { Component } from '@angular/core';
import { BookService } from '../book.service';
import { Book } from '../Book';

@Component({
  selector: 'app-view-abook',
  templateUrl: './view-abook.component.html',
  styleUrls: ['./view-abook.component.css']
})
export class ViewABookComponent {

  constructor(private bookservice:BookService){

  }
bookid!:number;
bookfind!:Book;
  viewAbook(){

    this.bookfind=this.bookservice.viewSingle(this.bookid);
    if(this.bookfind==undefined){
      alert("book id does not exist")
      this.bookid=0;
    }


  }

}
