import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {

  isUserLogged!:boolean;
  x=false;
  y=false;

  login(){
    if(this.isUserLogged==false){
      this.isUserLogged=true;
    }
    this.x=true;
    this.y=false;
  }

  register(){
    if(this.isUserLogged==true){
      this.isUserLogged=false;
    }
    this.x=false;
    this.y=true;
  }

}
<p>home works!</p>

<button (click)="login()" [disabled]="x">Login</button>
<button (click)="register()" [disabled]="y">Register</button>

<div *ngIf="isUserLogged==false";then mythenblock else myelseblock>

</div>

<ng-template #mythenBlock></ng-template>

