import { Injectable } from '@angular/core';
import { Book } from './Book';
import { Books } from './Books';

@Injectable({
  providedIn: 'root'
})
export class BooksService {

  constructor() { }

  actualboks:Books[]=
[{BookId:101,BookName:"Java",BookPrice:100},{BookId:102,BookName:"SQL",BookPrice:200}]


getallbooks():Books[]{

  return this.actualboks;

}

add(newbook:Books){
  this.actualboks.push(newbook)

}

delete(bookid:number):boolean{
  for(let eachbookarry of this.actualboks){
    if(eachbookarry.BookId=bookid){
      this.actualboks.splice(this.actualboks.indexOf(eachbookarry),1);
      return true;
    }

  }
  return false;
}

modify(book:Books):boolean{
  for(let eachbookarry of this.actualboks){
    if(eachbookarry.BookId==book.BookId){
      this.actualboks.splice(this.actualboks.indexOf(eachbookarry),1);
      this.actualboks.push(book);
      return true;
    }

  }
  return false;
}

view(bookid:number):Books{
  let bookfound!:Book;
  for(let index=0;index<this.actualboks.length;index++){
    const eachbookarry=this.actualboks[index];
    if(eachbookarry.BookId==bookid)
     bookfound=eachbookarry;
  }
  return bookfound;
}

}
