import { Component } from '@angular/core';
import { Bankpractice } from '../BankPractice';
import { BankAccountpracticeService } from '../bank-accountpractice.service';

@Component({
  selector: 'app-bank-accountpractice',
  templateUrl: './bank-accountpractice.component.html',
  styleUrls: ['./bank-accountpractice.component.css']
})
export class BankAccountpracticeComponent {

  source:Bankpractice={accno:101,name:"nivi",bal:2000};
  target:Bankpractice={accno:102,name:"yashu",bal:2000};
  amot!:number;

  constructor(private bs:BankAccountpracticeService){

  }

   transfer(){
    this.bs.transfer(this.source,this.target,this.amot);
   }



}
<p>bank-accountpractice works!</p>

<table border="5" [cellPadding]="5" [cellSpacing]="5">
    <tr>
        <th>number</th>
        <th>name</th>
        <th>balance</th>
    </tr>

    <tr>
        <td>{{source.accno}}</td>
        <td>{{source.name}}</td>
        <td>{{source.bal}}</td>
    </tr>


</table>
<table border="5" [cellPadding]="5" [cellSpacing]="5">
    <tr>
        <th>number</th>
        <th>name</th>
        <th>balance</th>
    </tr>

    <tr>
        <td>{{target.accno}}</td>
        <td>{{target.name}}</td>
        <td>{{target.bal}}</td>
    </tr>


</table>
<button> <input type= number (click)="transfer()">Transfer</button>
