import { Injectable } from '@angular/core';
import { Bank } from './Bank';

@Injectable({
  providedIn: 'root'
})
export class TransferService {

  constructor() { }
  source!:Bank;
  target!:Bank;
  amtra!:number;

  transferFunds(sourceAccount:Bank ,targetAccount:Bank,amountToTransfer:number){

    if(amountToTransfer==0 || amountToTransfer<0){
      alert("amount to tranfer cannot be negative or zero")
    }
    else{
      if(sourceAccount.AccountBalance==0){
        alert("Balance is insufficient");
      }
      else if(sourceAccount.AccountBalance<0){
        alert("Balance cannot be negative");
      }
      else{
        sourceAccount.AccountBalance-=amountToTransfer;
        targetAccount.AccountBalance+=amountToTransfer;
        alert("amount transferred succesefully");
      }


    }

  }

}
