import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
  name: 'palindrome'
})
export class PalindromePipe implements PipeTransform {

  transform(value:number): boolean {
     let temp=value;
     let rem=0;
     let rev=0;
     while(value>0){
      rem=value%10;
      rev=(rev*10)+rem;
      value=value/10;
     }
       if(temp==rev){
        return true;
       }
       return false;
  }

}
