import { Component } from '@angular/core';
import { BookService } from '../book.service';
import { Book } from '../Book';
import { Router } from '@angular/router';

@Component({
  selector: 'app-modify-book',
  templateUrl: './modify-book.component.html',
  styleUrls: ['./modify-book.component.css']
})
export class MOdifyBookComponent {

  constructor(private bookservice:BookService,private router:Router){

  }

  booktomodify:Book=new Book()
  bookfound!:boolean;
  modify(){
   this.bookfound= this.bookservice.modify(this.booktomodify);
    if(this.bookfound==true)
      alert('Book is edited successfully...');
    else
      alert('Book with this Id doesnot exists : ' + this.booktomodify.BookId);

  }



  }


<p>modify-book works!</p>
<table border="5" [cellSpacing]="5" [cellPadding]="5">
    <tr>
        Enter the Book Id<input type=number [(ngModel)]="booktomodify.BookId">
        Enter the Book Name<input type="text" [(ngModel)]="booktomodify.BookName">
        Enter the Book Price<input type=number [(ngModel)]="booktomodify.BookPrice">

    </tr>
</table>
<button (click)="modify()">Modify</button>
