import { Injectable } from '@angular/core';
import { Book } from './Book';

@Injectable({
  providedIn: 'root'
})
export class BookService {

  constructor() { }

   actualBooks:Book[]=[
    {BookId:101,BookName:"Java",BookPrice:100},
    {BookId:102,BookName:"SQL",BookPrice:200},
   ]

   getAllBooks():Book[]{
     return this.actualBooks;
   }

   addBook(newBook:Book){

      this.actualBooks.push(newBook);
   }

   delete(booktodelete:number):boolean{
            for(let eachbookarry of this.actualBooks){
              if(eachbookarry.BookId=booktodelete){
                this.actualBooks.splice(this.actualBooks.indexOf(eachbookarry),1)
                 return true;
              }
            }
            return false;

   }

   modify(booktomodify:Book):boolean{
    for(let eachbookarry of this.actualBooks){
      if(eachbookarry.BookId==booktomodify.BookId){
        this.actualBooks.splice(this.actualBooks.indexOf(eachbookarry),1)
        this.actualBooks.push(booktomodify);
        return true;
      }
    }
    return false;
   }
   viewAll(Bookid:number):Book{
    let bookfound!:Book;
    for(let index=0;index<this.actualBooks.length;index++){
      const eachbookarry=this.actualBooks[index];
      if(eachbookarry.BookId==Bookid)
      bookfound=eachbookarry;

    }
    return bookfound;


   }
}
